                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         using System;
using System.Collections.Generic;
using System.Text;

namespace Collection
{
    //-----------------------------------------------//
    class Employee
    {
        public int Id ;
        public string Name;
        public double Salary;
        public string Gender;
        public string Email;
        public string Address;

    //------------------------------------------------------------//
        public static void Main(string[] args)
        {
            Employee emp1 = new Employee()
            { Id = 101, Name = "Naresh", Salary = 12000, Gender = "Male", Email = "NareshIt@gmail.com", Address = "House No.-1"};

            Employee emp2 = new Employee()
            { Id = 102, Name = "Mahesh", Salary = 14000, Gender = "Male", Email = "MaheshIt@gmail.com", Address = "House No.-2"};

            Employee emp3 = new Employee()
            { Id = 103, Name = "Suresh", Salary = 16000, Gender = "Male", Email = "SureshIt@gmail.com", Address = "House No.-3"};

            Employee emp4 = new Employee()
            { Id = 104, Name = "Haresh", Salary = 18000, Gender = "Male", Email = "HareshIt@gmail.com", Address = "House No.-4"};

            Employee emp5 = new Employee()
            { Id = 105, Name = "Sumit", Salary = 20000, Gender = "Male", Email = "SumitIt@gmail.com", Address = "House No.-5"};

            Employee emp6 = new Employee()
            { Id = 106, Name = "Add", Salary = 12345, Gender = "Male", Email = "Added@gmail.com", Address = "House No.-6" };
            
 //----------------------------------------------------------------------------//

            List<Employee> op = new List<Employee>();
           
            op.Add(emp1);
            op.Add(emp2);
            op.Add(emp3);
            op.Add(emp4);
            op.Add(emp5);

            Console.WriteLine("Original List");
            foreach (var a in op)
            {
                Console.WriteLine(a.Id +" "+ a.Name +" "+ a.Salary +" "+ a.Gender +" "+ a.Email+" "+a.Address);
            }
            Console.WriteLine("===========================================================================================================");
//-------------------------------------------------------------------------------//
            List<Employee> pp = new List<Employee>();
            pp.Add(emp1);
            pp.Add(emp2);
            pp.Add(emp3);
            pp.Add(emp4);
            pp.Add(emp5);
            pp.Add(emp6);

            Console.WriteLine("After Addition");
            foreach (var a in pp)
            {
                Console.WriteLine(a.Id + " " + a.Name + " " + a.Salary + " " + a.Gender + " " + a.Email + " " + a.Address);
            }
            Console.WriteLine("===========================================================================================================");
//--------------------------------------------------------------------------//
            Console.WriteLine("Enter the Id you want to remove");
            int num = Convert.ToInt32(Console.ReadLine());
            foreach (var a in pp)
            {
                if (a.Id == num)
                {
                    pp.Remove(a);                                                                                                                                                                                                                                                                 
                    break;                                     
                }
            }
            foreach (var a in pp)
            {
                Console.WriteLine(a.Id + " " + a.Name + " " + a.Salary + " " + a.Gender + " " + a.Email + " " + a.Address);
            }
            Console.WriteLine("===========================================================================================================");

//--------------------------------------------------------------------------//

            Console.WriteLine("Enter the Id  you want to search");
            int search = Convert.ToInt32(Console.ReadLine());
            foreach (var b in pp)
            {
                if (search == b.Id || search.Equals( b.Name) || search == b.Salary ||search.Equals(b.Gender))
                {
                    Console.WriteLine(b.Id + " " + b.Name + " " + b.Salary + " " + b.Gender + " " + b.Email + " " + b.Address);
                }
            }

            Console.WriteLine("===========================================================================================================");
            Console.WriteLine("Enter the id which you want to update");
           string  old = Console.ReadLine();
            Console.WriteLine("Enter the email-id which you want to update");
            string update = Console.ReadLine();
            foreach (var b in pp)
            {
                if (old.Equals(b.Email))
                {
                    b.Email = update;
                    Console.WriteLine(b.Id + " " + b.Name + " " + b.Salary + " " + b.Gender + " " + b.Email + " " + b.Address);
                    break;
                }               
            }
        }
    }
}
