                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
using System;
using System.Collections.Generic;
using System.Text;

namespace Works
{

   internal interface IcustomerAutho
    {
        public void Login();
        public void Logout();
        public void ResetPassword();
    }
    internal interface IcustomerCrud
    {
        public void Create();
        public void Update();
        public void Delete();
        public void GetAll();
    }

    internal class CustomerService :IcustomerCrud,IcustomerAutho
    {
        //-----------------------------------------------//
        public void Login()
        {
            SendOtp();
            validateDbData();
            Console.WriteLine("Login Success");
        }
        public bool SendOtp()
        {
            Console.WriteLine("OTP Send");
            return true;
        }
        public bool validateDbData()
        {
            Console.WriteLine("Data Validate");
            return true;
        }
        //----------------------------------------------------------//

        public void Logout()
        {
            ClearSession();
            Console.WriteLine("LogOut Success");
        }
        public bool ClearSession()
        {
            Console.WriteLine("Session Cleared");
            return true;
        }
        //-----------------------------------------------------------------------//
        public void ResetPassword()
        {
            Console.WriteLine("Password Reset Changed");
        }
        //--------------------------------------------------------------------//
        public void Create()
        {
            Console.WriteLine("Customer Created");
        }

        public void Update()
        {
            Console.WriteLine("Customer Updated");
        }
        public void Delete()
        {
            Console.WriteLine("Customer Deleted");
        }
        public void GetAll()
        {
            Console.WriteLine("All Customer Readed");
        }
        //--------------------------------------------------------------------------------//
        public static void Main(string[] args)
        {
            IcustomerAutho autho = new CustomerService();
            autho.Login();

            IcustomerCrud crud = new CustomerService();
            crud.Create();
            crud.Delete();
        }
    }


}
\                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            